mapper.insert:
INSERT INTO myevent (
            id
          , ical_uid
          , keyword
          , category1
          , category2
          , category3
          , requester
          , content
          , sr_id
          , subsystem
          )
     VALUES (
            #{id}
          , #{iCalUid}
          , #{keyword}
          , #{category1}
          , #{category2}
          , #{category3}
          , #{requester}
          , #{content}
          , #{srId}
          , #{subsystem}
            )
         ON DUPLICATE KEY
     UPDATE keyword       = #{keyword}
          , category1     = #{category1}
          , category2     = #{category2}
          , category3     = #{category3}
          , requester     = #{requester}
          , content       = #{content}
          , sr_id         = #{srId}
          , subsystem     = #{subsystem}
          , modified_date = now()

INSERT INTO googleevent (
            id
          , ical_uid
          , etag
          , start
          , end
          , kind
          , creator_email
          , summary
          , description
          , location
          , html_link
          , created
          , updated
          )
   VALUES (
            #{id}
          , #{iCalUid}
          , #{etag}
          , #{start}
          , #{end}
          , #{kind}
          , #{creatorEmail}
          , #{summary}
          , #{description}
          , #{location}
          , #{htmlLink}
          , #{created}
          , #{updated}
          )
         ON DUPLICATE KEY
     UPDATE etag          = #{etag}
          , start         = #{start}
          , end           = #{end}
          , kind          = #{kind}
          , creator_email = #{creatorEmail}
          , summary       = #{summary}
          , description   = #{description}
          , location      = #{location}
          , html_link     = #{htmlLink}
          , created       = #{created}
